mapper.insert:
INSERT INTO SAMPLE
	( ID
	  , NAME
	  , DESCRIPTION
	  , USE_YN
	  , REG_USER )
VALUES ( #{id}
	  , #{name}
	  , #{description}
	  , #{useYn}
	  , #{regUser} )

mapper.update:
UPDATE SAMPLE
SET ID=#{id}
	, NAME=#{name}
	, DESCRIPTION=#{description}
	, USE_YN=#{useYn}
	  WHERE ID=#{id}

mapper.select:
SELECT item_id, item_name
FROM stock.item
WHERE item_id=#{itemId}

select a.item_id , b.item_name , deal_date , price_open , price_high , price_low , price_close , volume , price_close - pc as cpc , (price_close - pc) / pc * 100 as cpcp , agoD from ( select item_id , deal_date , price_open , price_high , price_low , price_close , volume , lag(price_close, 1) over(partition by item_id order by deal_date) as pc , lag(deal_date, 1) over(partition by item_id order by deal_date) as agoD from item_price where 1=1 and deal_date between str_to_date(#{searchDate}, '%Y%m%d') - interval 7 day and #{searchDate} ) as a inner join stock.item b on a.item_id = b.item_id where a.deal_date = #{searchDate}
and ( b.item_name like concat('%', #{searchKeyword}, '%') or b.item_id like concat('%', #{searchKeyword}, '%') )
order by a.item_id asc

SELECT COUNT(a.item_id) FROM stock.item a INNER JOIN stock.item_price b on a.item_id = b.item_id WHERE 1=1
AND b.deal_date = #{searchDate}
AND ( a.item_name LIKE concat('%', #{searchKeyword}, '%') or a.item_id LIKE concat('%', #{searchKeyword}, '%') )

SELECT min(price_low)
FROM stock.item_price
WHERE 1=1
		    AND item_id = #{chartId}
		    AND deal_date between str_to_date(#{searchDate}, '%Y%m%d') - interval 3 month and #{searchDate}

SELECT min(price_avg60)
FROM stock.item_price
WHERE 1=1
		    AND item_id = #{chartId}
		    AND deal_date between str_to_date(#{searchDate}, '%Y%m%d') - interval 3 month and #{searchDate}
